import React, { Component } from "react";
import Table from "./common/table.component";
import Rating from "./rating.component";

class Movies extends React.Component {
    state = {
        movies: [
            {rank: 1, title: 'The Shawshank Redemption', imdb_rating: '9.2', your_rating: true, action: ''},
            {rank: 2, title: 'The Godfather', imdb_rating: '9.1', your_rating: false, action: ''},
            {rank: 3, title: 'The Godfather: Part II', imdb_rating: '9.0', your_rating: true, action: ''},
            {rank: 4, title: 'The Dark Knight', imdb_rating: '9.0', your_rating: false, action: ''},
            {rank: 5, title: '12 Angry Men', imdb_rating: '8.9', your_rating: false, action: ''}
        ]        
    }

    handleToggleRating = (movieRank) => {

    }

  render() {
    const columns = [
        {label: 'Rank', path: 'rank', content: item => <td>{item}</td> },
        {label: 'Title', path: 'title', content: item => <td>{item}</td> },
        {label: 'IMDB Rating', path: 'imdb_rating', content: item => <td>{item}</td> },
        {label: 'Your Rating', path: 'your_rating', content: item => <td><Rating isRated={item}/></td> },
        {label: 'Action', path: 'action', content: item => <td>{item}</td> }
    ]
    return (
      <>
        <Table data={this.state.movies} columns={columns}/>
      </>
    );
  }
}

export default Movies;
