const TableHeader = ({ columns, sortColumn }) => { //sortColumn = { path: id, order: desc/asc }
  console.log(sortColumn);
  return (
    <>
      <thead>
        <tr>
          {columns.map((col) => {
            
            return (
              <>
                <th>{col.label} {sortColumn.path === col.path ? sortColumn.order === 'asc' ? <i className="bi bi-sort-down"></i> : <i className="bi bi-sort-down-alt"></i> : null}</th>

              </>
            );
          })}
        </tr>
      </thead>
    </>
  );
};

export default TableHeader;
