import React, { Component } from "react";
import Table from "./common/table.component";
import Rating from "./rating.component";
import getMovies from "../service/get-movies.service";
import _ from "lodash";
import Pagination from "./common/pagination.component";

class Movies extends React.Component {
  state = {
    movies: [],
    sortColumn: { path: "id", order: "asc" },
	activePage: 1,
	pageCount: 5
  };

  componentDidMount() {
    const movies = getMovies();
    this.setState({ ...this.state, movies });
  }

  handleToggleRating = (movieRank) => {
    const movies = [...this.state.movies];
    const movie = movies.find((movie) => movie.id === movieRank);
    movie.your_rating = !movie.your_rating;
    this.setState({ ...this.state, movies });
  };

  handleSort = (sortColumn) => {
    this.setState({ ...this.state, sortColumn });
  };

  handleClickPage = (activePage) => {
	  this.setState({ ...this.state, activePage })
  }

  paginateMovies = (movies) => {
	const { activePage, pageCount } = this.state;
	const start = (activePage - 1) * pageCount;
	const paginatedMovies = movies.slice(start, start + pageCount);
	return paginatedMovies;
  }

  sortMovies = (movies) => {
    const { sortColumn } = this.state;
    const sortedMovies = _.orderBy(
      movies,
      [sortColumn.path],
      [sortColumn.order]
    );
    return sortedMovies;
  };

  render() {
	const paginatedMovies = this.paginateMovies(this.state.movies);
    const movies = this.sortMovies(paginatedMovies);
    const columns = [
      {
        label: "Rank",
        path: "id",
        sorting: true,
        content: (movie, key) => <td>{movie[key]}</td>,
      },
      {
        label: "Title",
        path: "title",
        sorting: true,
        content: (movie, key) => <td>{movie[key]}</td>,
      },
      {
        label: "Poster",
        path: "posterUrl",
        content: (movie, key) => (
          <td>
            <img src={movie[key]} style={{ height: "100px", width: "auto" }} />
          </td>
        ),
      },
      {
        label: "Your Rating",
        path: "your_rating",
        content: (movie, key) => (
          <td>
            <Rating
              isRated={movie[key]}
              rank={movie.id}
              handleToggleRating={this.handleToggleRating}
            />
          </td>
        ),
      },
      {
        label: "Action",
        path: "action",
        content: (movie, key) => <td>{movie[key]}</td>,
      },
    ];
    return (
      <>
	  	<div>

		</div>
        <Table
          items={movies}
          columns={columns}
          onSort={this.handleSort}
          sortColumn={this.state.sortColumn}
        />
		<Pagination totalItems={this.state.movies.length} pageCount={this.state.pageCount} activePage={this.state.activePage} onClickPage={this.handleClickPage}/>
      </>
    );
  }
}

export default Movies;
