import React, { Component } from "react";
import Table from "./common/table.component";
import Rating from "./rating.component";
import getMovies from "../service/get-movies.service";

class Movies extends React.Component {
    state = {
        movies: []        
    }

    componentDidMount() {
      const movies = getMovies();
      this.setState({ movies });
    }

    handleToggleRating = movieRank => {
      const movies = [...this.state.movies];
      const movie = movies.find(movie => movie.rank === movieRank)
      movie.your_rating = !(movie.your_rating);
      this.setState({ movies });
    }

  render() {
    const columns = [
        {
          label: 'Rank', 
          path: 'id', 
          content: (movie,key) => <td>{movie[key]}</td> 
        },
        {
          label: 'Title', 
          path: 'title', 
          content:(movie,key) => <td>{movie[key]}</td> 
        },
        {
          label: 'IMDB Rating', 
          path: 'imdb_rating', 
          content: (movie,key) => <td>{movie[key]}</td> 
        },
        {
          label: 'Your Rating', 
          path: 'your_rating', 
          content: (movie,key) => <td><Rating 
                                        isRated={movie[key]} 
                                        rank={movie.rank} 
                                        handleToggleRating={this.handleToggleRating}/>
                                  </td> },
        {
          label: 'Action', 
          path: 'action', 
          content: (movie,key) => <td>{movie[key]}</td> 
        }
    ]
    return (
      <>
        <Table data={this.state.movies} columns={columns}/>
      </>
    );
  }
}

export default Movies;
