import React, { Component } from 'react';

class Rating extends React.Component {
    state = {
        isHovered: false
    }

    handleHover = () => {
        this.setState({ isHovered: true });
        console.log('Hovered')
    }
    render() { 
        const isRated = this.props.isRated;
        return (
            <>
                <i onMouseOver={this.handleHover} className={this.props.isRated ? "bi bi-star-fill" : "bi bi-star text-primary"}></i>
            </>
        );
    }
}
 
export default Rating;
